mapper.select:
SELECT c.*, m.nickname as leader_name
FROM club c JOIN member m ON c.club_leader = m.member_id 
WHERE club_id = #{clubId}

SELECT * FROM (SELECT A.* , ROWNUM rn FROM ( SELECT club_id , club_name , club_leader , club_people , club_date , fn_get_code_name(open_scope) AS open_scope FROM club
AND open_scope = #{openScope}
AND ( title LIKE '%'||#{search}||'%' OR question_id LIKE '%'||#{search}||'%' OR member_id LIKE '%'||#{search}||'%' ) AND open_scope LIKE '%'||#{openScope}||'%'
ORDER BY club_id ) A) b WHERE rn BETWEEN (#{page}-1)*#{pageUnit} + 1 and #{page} * #{pageUnit}

mapper.insert:

	  		    SELECT NVL(MAX(club_id),0) + 1
				FROM club

  		INSERT into club 
  		VALUES 
  			(
  				#{clubId}
  				,#{clubName} 
  				,#{clubIntro}
  				,#{clubProfileImg}
  				,#{clubPeople}
  				,#{joinCondition}
  				,#{openScope}
  				,null
  				,#{clubLeader}
  				,sysdate
  			)

mapper.update:
UPDATE club
SET club_leader = #{clubLeader}
WHERE club_id = #{clubId}

UPDATE club
SET 
	club_name = #{clubName}
	, club_intro = #{clubIntro}
	, club_people = #{clubPeople}
	, join_condition = #{joinCondition}
	, open_scope = #{openScope}
WHERE club_id = #{clubId}

UPDATE club
SET
	club_profile_img = #{clubProfileImg}
WHERE club_id = #{clubId}
