mapper.update:
	    UPDATE club_member 
      SET join_date = null
      WHERE club_id = #{clubId}
      AND member_id = #{memberId}

		UPDATE club_member 
      SET join_date = sysdate, stop_date = null 
      WHERE club_id = #{clubId}
      AND member_id = #{memberId}

UPDATE club_member
SET join_date = sysdate
WHERE club_id = #{clubId}
AND member_id = #{memberId}

mapper.select:
SELECT *
FROM club_member 
WHERE club_id = #{clubId} 
AND member_id = #{memberId}

mapper.update:
UPDATE club_member 
SET stop_date = sysdate 
WHERE club_id = #{clubId}
AND member_id = #{memberId}
