mapper.insert:
INSERT INTO member
			(
				member_id
				, member_name
				, password
				, nickname
				, addr
				, detail_addr
				, phone
				, join_date
				, role
				, sns_yn
			)
	VALUES  (
				#{memberId}
				, #{memberName}
				, #{password}
				, #{nickname}
				, #{addr}
				, #{detailAddr}
				, #{phone}
				, sysdate
				, #{role}
				, #{snsYn}
			)

mapper.select:
SELECT join_date 
FROM member 
WHERE member_id = #{memberId}

SELECT member_id
FROM member
WHERE member_id = #{memberId}

SELECT nickname
FROM member
WHERE nickname = #{nickname}

SELECT member_id
FROM member
WHERE member_name = #{memberName}
AND member_id = #{memberId}
AND phone = #{phone}

mapper.update:
UPDATE member
SET password = #{authNum}
WHERE member_id = #{memberId}
