mapper.insert:
INSERT INTO member
			(
				member_id
				, member_name
				, password
				, nickname
				, addr
				, phone
				, join_date
				, role
				, sns_yn
			)
	VALUES  (
				#{memberId}
				, #{memberName}
				, #{password}
				, #{nickname}
				, #{addr}
				, #{phone}
				, sysdate
				, #{role}
				, #{snsYn}
			)

mapper.select:
SELECT join_date 
FROM member 
WHERE member_id = #{memberId}
