mapper.insert:
INSERT INTO member
			(
				member_id
				, member_name
				, password
				, nickname
				, addr
				, detail_addr
				, phone
				, join_date
				, role
				, sns_yn
			)
	VALUES  (
				#{memberId}
				, #{memberName}
				, #{password}
				, #{nickname}
				, #{addr}
				, #{detailAddr}
				, #{phone}
				, sysdate
				, #{role}
				, #{snsYn}
			)

mapper.select:
SELECT join_date 
FROM member 
WHERE member_id = #{memberId}

SELECT member_id
FROM member
WHERE member_id = #{memberId}

SELECT nickname
FROM member
WHERE nickname = #{nickname}

SELECT member_id
FROM member
WHERE member_name = #{memberName}
AND member_id = #{memberId}
AND phone = #{phone}

mapper.update:
UPDATE member
SET password = #{authNum}
	, login_fail_cnt = 0
WHERE member_id = #{memberId}

		UPDATE member
		SET login_fail_cnt = login_fail_cnt + 1
        WHERE member_id = #{memberId}

		UPDATE member
		SET login_fail_cnt = 0
        WHERE member_id = #{memberId}

UPDATE member

	member_name = #{memberName}
	password = #{password}
	nickname = #{nickname}
	addr = #{addr}
	detail_addr = #{detailAddr}
	phone = #{phone}
	stop_date = sysdate
	role = #{role}
	profile_img = #{profileImg}
	member_intro = #{memberIntro}
	report_cnt = #{reportCnt}
	lock_yn = #{lockYn}

WHERE member_id = #{memberId}
