mapper.insert:

	SELECT NVL(MAX(memo_id),0) + 1
	FROM memo

INSERT INTO memo
	(
		memo_id
		, seq_no
		, color
		, content
		, hash_tag
		, bookmark
		, write_date
		, member_id
	)
VALUES
	(
		#{memoId}
		, #{seqNo}
		, #{color}
		, #{content}
		, #{hashTag}
		, #{bookmark}
		, #{writeDate}
		, #{memberId}
	)

mapper.update:
UPDATE memo
	SET
		seq_no = #{seqNo}
		, color = #{color}
		, content = #{content}
		, hash_tag = #{hashTag}
		, bookmark = #{bookmark}
		, update_date = #{updateDate}
		, member_id = #{memberId}
WHERE memo_id = #{memoId}
AND member_id = #{memberId}

mapper.delete:
DELETE FROM memo
WHERE memo_id = #{memoId}
AND member_id = #{memberId}
