mapper.select:
SELECT * FROM (SELECT A.* , ROWNUM rn FROM ( SELECT * FROM ( SELECT q.question_id, q.title, q.member_id, q.write_date, fn_get_code_name(question_type) AS question_type, CASE WHEN ( SELECT COUNT(*) FROM answer a WHERE a.question_id = q.question_id ) = 0 THEN '답변대기' ELSE '답변완료' END AS question_yn FROM question q ) question
AND question_yn = #{questionYn}
AND ( title LIKE '%'||#{search}||'%' OR question_id LIKE '%'||#{search}||'%' OR member_id LIKE '%'||#{search}||'%' ) AND question_yn LIKE '%'||#{questionYn}||'%'
ORDER BY question_id DESC ) A) b WHERE rn BETWEEN (#{page}-1)*#{pageUnit} + 1 and #{page} * #{pageUnit}

SELECT COUNT(*) FROM ( SELECT q.question_id, q.title, q.member_id, q.write_date, fn_get_code_name(question_type) AS question_type, CASE WHEN ( SELECT COUNT(*) FROM answer a WHERE a.question_id = q.question_id ) = 0 THEN '답변대기' ELSE '답변완료' END AS question_yn FROM question q ) question
AND question_yn = #{questionYn}
AND (title LIKE '%'||#{search}||'%' OR question_id LIKE '%'||#{search}||'%' OR member_id LIKE '%'||#{search}||'%') AND question_yn LIKE '%'||#{questionYn}||'%'


    SELECT * FROM (
    SELECT Q.*, ROWNUM AS rnum
    FROM (
        SELECT *
        FROM QUESTION
        WHERE MEMBER_ID = #{memberId}
        ORDER BY WRITE_DATE DESC
    ) Q
    WHERE ROWNUM <= #{limit}
) WHERE rnum > #{offset}

SELECT count(*)
FROM QUESTION
WHERE MEMBER_ID =#{memberId}
