mapper.select:
SELECT * FROM ( SELECT A.* , ROWNUM rn FROM ( SELECT * FROM ( SELECT report_id, reporter_id, report_content, report_date, fn_get_code_name(report_type) AS report_type, NVL(TO_CHAR(fn_get_code_name(report_process_type)),'신고대기') AS report_process_type FROM report) report
report_process_type = #{reportProcessType}
AND ( report_content LIKE '%'||#{search}||'%' OR report_id LIKE '%'||#{search}||'%' OR reporter_id LIKE '%'||#{search}||'%' OR report_type LIKE '%'||#{search}||'%' )
ORDER BY report_id DESC) A) b WHERE rn BETWEEN (#{page}-1)*#{pageUnit} + 1 and #{page} * #{pageUnit}

SELECT COUNT(*) FROM ( SELECT report_id, reporter_id, report_content, report_date, fn_get_code_name(report_type) AS report_type, fn_get_code_name(report_process_type) AS report_process_type FROM report) report
report_process_type = #{reportProcessType}
AND ( report_content LIKE '%'||#{search}||'%' OR report_id LIKE '%'||#{search}||'%' OR reporter_id LIKE '%'||#{search}||'%' OR report_type LIKE '%'||#{search}||'%' )

mapper.insert:

	SELECT NVL(MAX(report_id),0) + 1 as reportId
	FROM report

INSERT INTO report
	(
		report_id
		, menu_type
		, post_id
		, report_type
		, report_date
		, reporter_id
		, reported_id
		, report_content
	)
VALUES
	(
		#{reportId}
		, #{menuType}
		, #{postId}
		, #{reportType}
		, sysdate
		, #{reporterId}
		, #{reportedId}
		, #{reportContent}
	)

mapper.update:
update member
set report_cnt = report_cnt + 1
where member_id = #{reportedId}

update member
set report_cnt = 0
where member_id = #{reportedId}
