mapper.select:
SELECT count(*) 
FROM review 
WHERE club_id = #{clubId} 
AND member_id = #{memberId}

mapper.insert:

    SELECT NVL(MAX(REVIEW_ID)+1,0) FROM REVIEW

INSERT INTO review (
					review_id
					, write_date
					, star
					, club_id
					, member_id
					, contents
					)
			VALUES (
					#{reviewId}
					, sysdate
					, #{star}
					, #{clubId}
					, #{memberId}
					, #{contents}
					)

mapper.select:
  		 SELECT ROUND(NVL(AVG(star), 0), 1) AS avg_star
		 FROM review
		 WHERE club_id = #{clubId}
