mapper.select:
SELECT count(*) 
FROM review 
WHERE club_id = #{clubId} 
AND member_id = #{memberId}

mapper.insert:

    SELECT MAX(REVIEW_ID)+1 FROM REVIEW

INSERT INTO review (
					review_id
					, write_date
					, star
					, club_id
					, member_id
					, contents
					)
			VALUES (
					#{reviewId}
					, sysdate
					, #{star}
					, #{clubId}
					, #{memberId}
					, #{contents}
					)
