mapper.insert:

	SELECT NVL(MAX(trade_id),0) + 1 as tradeId
	FROM trade

INSERT INTO trade
	(
	trade_id
	, title
	, description
	, trade_category

		, price

	, open_kakao

		, trade_place

	, trade_type
	, payment_type

		, latitude


		, longitude

	, seller_id
	)
VALUES
	(
	#{tradeId}
	, #{title}
	, #{description}
	, #{tradeCategory}

		, #{price}

	, #{openKakao}

		, #{tradePlace}

	, #{tradeType}
	, #{paymentType}

		, #{latitude}


		, #{longitude}

	, #{sellerId}
	)

mapper.update:
UPDATE trade
title = #{title}, description = #{description}, trade_category = #{tradeCategory}, price = #{price}, open_kakao = #{openKakao}, trade_place = #{tradePlace}, trade_type = #{tradeType}, payment_type = #{paymentType}, update_date = sysdate, latitude = #{latitude}, longitude = #{longitude}
WHERE trade_id = #{tradeId} AND seller_id = #{sellerId}

UPDATE trade 
SET views = views + 1
WHERE trade_id = #{tradeId}
AND seller_id = #{sellerId}

mapper.delete:
DELETE FROM trade
WHERE
trade_id = #{tradeId}
AND seller_id = #{sellerId}

mapper.insert:
{call insert_pay_point(
	#{memberId, mode=IN, jdbcType=VARCHAR},
	#{point, mode=IN, jdbcType=INTEGER},
	#{tradeId, mode=IN, jdbcType=INTEGER},
	#{holoPay, mode=IN, jdbcType=INTEGER}
)}

mapper.select:
SELECT t.trade_id , t.title , fn_get_code_name(trade_category) as trade_category , t.price , fn_get_code_name(trade_type) as trade_type , fn_get_code_name(promise_status) as promise_status , t.write_date , t.seller_id
, a.save_file FROM trade t JOIN attachment a ON(t.trade_id = a.post_id) JOIN heart h ON(t.trade_id = h.trade_id) WHERE a.menu_type = 'AA1' AND a.attachment_seq = (select min(attachment_seq) from attachment where post_id = t.trade_id) AND h.member_id = #{memberId}
FROM trade t WHERE seller_id = #{sellerId}
FROM trade t WHERE buyer_id = #{buyerId}
ORDER BY write_date desc
